import React, { useState } from "react";
import Axios from 'axios';

export const Login = () => {
    const [useridReg, setUserIdReg] = useState('');
    const [passwordReg, setPasswordReg] = useState('');
    const [userTypeReg, setUserTypeReg] = useState('');

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const register = () => {
        Axios.post('http://localhost:3002/api/register', {
            username: useridReg,
            password: passwordReg,
            usertype: userTypeReg
        }).then((response) => {
            console.log(response);
        });
    };

    const login = () => {
        Axios.post('http://localhost:3002/api/login', {
            username: username,
            password: password,
        }).then((response) => {
            console.log(response);
        });
    };



    return (
        <div id="auth-form-container">
            <p id="title">Welcome to the Employee Time Management System </p>
            <form id="login-form">
                <label>User ID</label>
                <input 
                    type="text"
                    onChange={(e) => {
                        setUserIdReg(e.target.value);
                    }}
                value={useridReg} />
                <label>Password</label>
                <input 
                    type="text"
                    onChange={(e) => {
                        setPasswordReg(e.target.value);
                    }} />
                <label >Employee Type</label>
                <input 
                    type="text"
                    onChange={(e) => {
                        setUserTypeReg(e.target.value);
                    }}
                 />

                <button onClick={register}>Register</button>

            </form>

        {<form id="login-form">
                <label>User ID</label>
                <input value={username} onChange={(e) => setUsername(e.target.value)} type="text"  />
                <label >Password</label>
                <input value={password} onChange={(e) => setPassword(e.target.value)} type="password"  />
                <button onClick={login}>Login</button>
         </form>}


        </div>
    )
}
